"generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

model post {
  id        Int      @id @default(autoincrement())
  created_at DateTime @default(now())
  updated_at DateTime @updatedAt
  title     String   @db.VarChar(255)
  content   String?
  published Boolean  @default(false)
  author    user     @relation(fields: [author_id], references: [id])
  author_id  Int
  @@index([title,content])
}

model profile {
  id     Int     @id @default(autoincrement())
  bio    String?
  user   user    @relation(fields: [user_id], references: [id])
  user_id Int     @unique
}

model user {
  id      Int      @id @default(autoincrement())
  email   String   @unique
  name    String?
  posts   post[]
  profile profile?
}

"

"generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}


model movie {
  id          Int       @id @default(autoincrement())
  title       String
  duration    Int       // Duration in minutes
  showtimes   showtime[]
}

model cinema {
  id          Int       @id @default(autoincrement())
  name        String
  location    String
  halls       hall[]
}

model hall {
  id          Int       @id @default(autoincrement())
  name        String
  cinema_id    Int
  cinema      cinema    @relation(fields: [cinema_id], references: [id])
  seats       seat[]
  showtimes   showtime[]
}

model seat {
  id          Int       @id @default(autoincrement())
  number      String
  hall_id      Int
  hall        hall      @relation(fields: [hall_id], references: [id])
  tickets     ticket[]
}

model showtime {
  id          Int       @id @default(autoincrement())
  startTime   DateTime
  movieId     Int
  movie       movie     @relation(fields: [movieId], references: [id])
  hall_id      Int
  hall        hall      @relation(fields: [hall_id], references: [id])
  tickets     ticket[]
}

model ticket {
  id          Int       @id @default(autoincrement())
  customer_id  Int?
  customer    customer? @relation(fields: [customer_id], references: [id])
  showtim_id  Int
  showtime    showtime  @relation(fields: [showtim_id], references: [id])
  seat_id      Int
  seat        seat      @relation(fields: [seat_id], references: [id])
  price       Float
}

model customer {
  id          Int       @id @default(autoincrement())
  name        String
  email       String    @unique
  tickets     ticket[]
}"

"generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

model client {
  id        Int      @id @default(autoincrement())
  name      String   @unique
  email     String   @unique
  projects  project[]
}

model project {
  id          Int      @id @default(autoincrement())
  name        String
