"generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

model data_center {
  id          Int       @id @default(autoincrement())
  name        String    @unique
  location    String
  servers     server[]
  incidents   incident[]
}

model server {
  id          Int       @id @default(autoincrement())
  hostname    String    @unique
  ip_address   String    @unique
  data_center_id Int
  data_center  data_center @relation(fields: [data_center_id], references: [id])
  resources   resource[]
  incidents   incident[]
}

model resource {
  id          Int       @id @default(autoincrement())
  type        String
  usage       Float
  server_id    Int
  server      server    @relation(fields: [server_id], references: [id])
}

model incident {
  id          Int       @id @default(autoincrement())
  description String
  resolved    Boolean   @default(false)
  server_id    Int?
  server      server?   @relation(fields: [server_id], references: [id])
  data_center_id Int?
  data_center  data_center? @relation(fields: [data_center_id], references: [id])
  @@index([description,resolved])
}"

"generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

model project {
  id          Int          @id @default(autoincrement())
  name        String
  description String
  client_id    Int
  client      client       @relation(fields: [client_id], references: [id])
  deliverables deliverable[]
  @@index([name, description])
}

model client {
  id       Int       @id @default(autoincrement())
  name     String
  email    String    @unique
  projects project[]
}

model deliverable {
  id          Int      @id @default(autoincrement())
  title       String
  description String
  project_id   Int
  project     project  @relation(fields: [project_id], references: [id])
  status_id    Int
  status      status   @relation(fields: [status_id], references: [id])
  @@index([title, description])
}

model status {
  id           Int           @id @default(autoincrement())
  name         String
  description  String
  deliverables deliverable[]
  @@index([name, description])
}

