"generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

model gyms {
  id            Int            @id @default(autoincrement())
  name          String         @unique
  memberships   memberships[]
  classes       classes[]
  gym_trainers  gym_trainers[]
}

model memberships {
  id        Int    @id @default(autoincrement())
  gym_id    Int
  member_id Int
  start_date DateTime
  end_date   DateTime?
  gym        gyms   @relation(fields: [gym_id], references: [id])
  member     members @relation(fields: [member_id], references: [id])
}

model classes {
  id        Int    @id @default(autoincrement())
  gym_id    Int
  name      String
  schedule  DateTime
  gym       gyms   @relation(fields: [gym_id], references: [id])
  trainers  trainers[]
}

model gym_trainers {
  gym_id    Int
  trainer_id Int
  gym       gyms     @relation(fields: [gym_id], references: [id])
  trainer   trainers @relation(fields: [trainer_id], references: [id])
  @@id([gym_id, trainer_id])
}

model trainers {
  id            Int            @id @default(autoincrement())
  name          String
  expertise     String
  gym_trainers  gym_trainers[]
  classes       classes[]
}

model members {
  id          Int          @id @default(autoincrement())
  name        String       @unique
  email       String       @unique
  memberships memberships[]
}"

"generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

model hospital {
  id            Int             @id @default(autoincrement())
  name          String          @unique
  address       String
  patients      patient[]
}

model patient {
  id            Int             @id @default(autoincrement())
  name          String
  date_of_birth DateTime
  hospital_id   Int
  medical_records medical_record[]
  appointments  appointment[]
  hospital      hospital         @relation(fields: [hospital_id], references: [id])
}

model medical_record {
  id            Int             @id @default(autoincrement())
  record_date   DateTime
  details       String
  patient_id    Int
  patient       patient         @relation(fields: [patient_id], references: [id])
}

model appointment {
  id            Int             @id @default(autoincrement())
  appointment_date DateTime
  purpose       String
  patient_id    Int
  doctor_id     Int
  patient       patient         @relation(fields: [patient_id], references: [id])
  doctor        doctor          @relation(fields: [doctor_id], references: [id])
  @@index([patient_id, doctor_id])
}

model doctor {
  id            Int             @id @default(autoincrement())
  name          String
  specialty     String
  appointments  appointment[]
}"

"generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

model insurance_policy {
  id                Int               @id @default(autoincrement())
  policy_number     String            @unique
  insured_id        Int
  insured           insured_data      @relation(fields: [insured_id], references: [id])
  claims            claim[]
}

model insured_data {
  id                Int               @id @default(autoincrement())
  full_name         String
  date_of_birth     DateTime
  policies          insurance_policy[]
}

model claim {
  id                Int               @id @default(autoincrement())
  claim_number      String            @unique
  policy_id         Int
  policy            insurance_policy  @relation(fields: [policy_id], references: [id])
  claim_details     String
  date_of_claim     DateTime
  user_id           Int
  user              user              @relation(fields: [user_id], references: [id])
}

model user {
  id                Int               @id @default(autoincrement())
  username          String            @unique
  email             String            @unique
  claims            claim[]
}"

