model book {
  id        Int      @id @default(autoincrement())
  title     String
  author    String
  loans     loan[]
  @@index([title,author])
}

model user {
  id        Int      @id @default(autoincrement())
  name      String
  loans     loan[]
}

model loan {
  id        Int      @id @default(autoincrement())
  bookId    Int
  userId    Int
  book      book     @relation(fields: [bookId], references: [id])
  user      user     @relation(fields: [userId], references: [id])
